import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserDashboard = () => {
  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    axios.get('/api/usuarios')
      .then(response => {
        setUsuarios(response.data);
      })
      .catch(error => {
        console.error('Houve um erro ao buscar os usuários!', error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/usuarios/${id}`)
      .then(response => {
        setUsuarios(usuarios.filter(usuario => usuario.id !== id));
      })
      .catch(error => {
        console.error('Houve um erro ao excluir o usuário!', error);
      });
  };

  return (
    <div>
      <h2>Painel de Usuários</h2>
      <ul>
        {usuarios.map(usuario => (
          <li key={usuario.id}>
            {usuario.nome} - {usuario.email}
            <button onClick={() => handleDelete(usuario.id)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserDashboard;
